import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { DataService, Specialization, Skill } from '../../services/data.service';

@Component({
  selector: 'app-skills',
  templateUrl: './skills.component.html',
  styleUrls: ['./skills.component.scss']
})
export class SkillsComponent implements OnInit {
  specialization?: Specialization;
  selectedSkills: { [key: number]: boolean } = {};

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private dataService: DataService
  ) {}

  ngOnInit(): void {
    const id = Number(this.route.snapshot.paramMap.get('id'));
    this.dataService.getSpecializationById(id).subscribe((spec) => {
      this.specialization = spec;
    });
  }

  toggleSkill(skillId: number) {
    this.selectedSkills[skillId] = !this.selectedSkills[skillId];
  }

  continue() {
    // ŸÖÿ®ÿØÿ¶ŸäŸãÿß ŸÅŸÇÿ∑ ŸÜÿ∑ÿ®ÿπ ÿßŸÑŸÖŸáÿßÿ±ÿßÿ™ ÿßŸÑŸÖÿÆÿ™ÿßÿ±ÿ© ŸÅŸä ÿßŸÑŸÉŸàŸÜÿ≥ŸàŸÑ
    const selected = Object.keys(this.selectedSkills)
      .filter(id => this.selectedSkills[Number(id)])
      .map(Number);
    console.log('Selected skills:', selected);

    // ŸÑÿßÿ≠ŸÇŸãÿß ÿ≥ŸÜŸÜŸÇŸÑŸáŸÖ ÿ•ŸÑŸâ ÿµŸÅÿ≠ÿ© ÿßŸÑŸÜÿ™Ÿäÿ¨ÿ©
    this.router.navigate(['/result'], {
      state: { selected, specialization: this.specialization }
    });
  }
}
<div class="skills-container" *ngIf="specialization">
  <h2>üß† ÿ™ÿÆÿµÿµ: {{ specialization?.title }}</h2>
  <p>{{ specialization?.description }}</p>

  <h3>ÿßÿÆÿ™ÿ± ÿßŸÑŸÖŸáÿßÿ±ÿßÿ™ ÿßŸÑÿ™Ÿä ÿ™ÿ™ŸÇŸÜŸáÿß:</h3>
  <div class="skills-list">
    <div
      class="skill-card"
      *ngFor="let skill of specialization.requirements"
      [class.selected]="selectedSkills[skill.id]"
      (click)="toggleSkill(skill.id)"
    >
      <span>{{ skill.name }}</span>
    </div>
  </div>

  <button type="button" class="continue-btn" (click)="continue()">ÿßŸÑÿ™ÿßŸÑŸä ‚û°Ô∏è</button>
</div>

