import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { DataService, Specialization } from '../../services/data.service';

@Component({
  selector: 'app-result',
  templateUrl: './result.component.html',
  styleUrls: ['./result.component.scss']
})
export class ResultComponent implements OnInit {
  specialization?: Specialization;
  userSkills: string[] = [];
  matchPercentage: number = 0;
  recommendedCourses: string[] = [];

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private dataService: DataService
  ) {}

  ngOnInit() {
    const id = Number(this.route.snapshot.paramMap.get('id'));
    this.dataService.getSpecializationById(id).subscribe((spec) => {
      this.specialization = spec;

      // üëá ÿßŸÑŸÖŸáÿßÿ±ÿßÿ™ ÿßŸÑŸÖÿÆÿ™ÿßÿ±ÿ© ŸÖŸÜ ÿµŸÅÿ≠ÿ© Skills (ÿ¨ÿßŸäŸäŸÜ ŸÖŸÜ state)
      const state = history.state;
      this.userSkills = state.userSkills || [];

      if (spec) {
        this.calculateMatch(spec);
        this.generateRecommendations(spec);
      }
    });
  }

  calculateMatch(spec: Specialization) {
    // ŸÜÿ≠ÿ≥ÿ® ÿßŸÑÿ™ŸàÿßŸÅŸÇ ÿ®ŸÜÿßÿ° ÿπŸÑŸâ ÿßŸÑŸÖŸáÿßÿ±ÿßÿ™ ÿßŸÑŸÑŸä ÿπŸÜÿØ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
    const matched = spec.requirements.filter(req =>
      this.userSkills.includes(req.name)
    ).length;

    this.matchPercentage = Math.round((matched / spec.requirements.length) * 100);
  }

  generateRecommendations(spec: Specialization) {
    const missingSkills = spec.requirements
      .filter(req => !this.userSkills.includes(req.name))
      .map(req => req.name);

    // ‚ú® ŸÖÿ´ÿßŸÑ ÿ®ÿ≥Ÿäÿ∑ ŸÑŸÑÿØŸàÿ±ÿßÿ™ ÿ®ŸÜÿßÿ°Ÿã ÿπŸÑŸâ ÿßŸÑŸÖŸáÿßÿ±ÿßÿ™ ÿßŸÑŸÜÿßŸÇÿµÿ©
    this.recommendedCourses = missingSkills.map(skill => `ÿØŸàÿ±ÿ© ŸÑÿ™ÿπŸÑŸÖ ${skill}`);
  }

  goBack() {
    this.router.navigate(['/specializations']);
  }
}
<div class="result-container" *ngIf="specialization">
  <h2>ŸÜÿ™Ÿäÿ¨ÿ™ŸÉ ŸÅŸä ÿ™ÿÆÿµÿµ: {{ specialization?.title }}</h2>

  <div class="match-section">
    <h3>ŸÜÿ≥ÿ®ÿ© ÿßŸÑÿ™ŸàÿßŸÅŸÇ</h3>
    <div class="match-circle">
      {{ matchPercentage }}%
    </div>
  </div>

  <div class="skills-section">
    <h3>ÿßŸÑŸÖŸáÿßÿ±ÿßÿ™ ÿßŸÑÿ™Ÿä ÿ™ŸÖÿ™ŸÑŸÉŸáÿß</h3>
    <ul>
      <li *ngFor="let skill of userSkills">{{ skill }}</li>
    </ul>

    <h3>ÿßŸÑŸÖŸáÿßÿ±ÿßÿ™ ÿßŸÑŸÖŸÇÿ™ÿ±ÿ≠ÿ© ŸÑŸÑÿ™ÿ∑ŸàŸäÿ±</h3>
    <ul>
      <li *ngFor="let course of recommendedCourses">{{ course }}</li>
    </ul>
  </div>

  <button (click)="goBack()" class="back-btn">üîô ÿπŸàÿØÿ© ŸÑŸÑÿ™ÿÆÿµÿµÿßÿ™</button>
</div>
