import { Injectable } from '@angular/core';
import { Observable, of } from 'rxjs';

export interface Skill {
  id: number;
  name: string;
  requiredLevel: number;
}

export interface Specialization {
  id: number;
  title: string;
  description: string;
  requirements: Skill[];
}

@Injectable({
  providedIn: 'root'
})
export class DataService {
  private specializations: Specialization[] = [
    {
      id: 1,
      title: 'Frontend Developer',
      description: 'Builds user interfaces using HTML, CSS, JavaScript, and frameworks like Angular or React.',
      requirements: [
        { id: 1, name: 'HTML & CSS', requiredLevel: 80 },
        { id: 2, name: 'JavaScript', requiredLevel: 70 },
        { id: 3, name: 'Angular', requiredLevel: 60 }
      ]
    },
    {
      id: 2,
      title: 'Data Analyst',
      description: 'Analyzes data and builds insights using SQL, Excel, and Python.',
      requirements: [
        { id: 4, name: 'SQL', requiredLevel: 80 },
        { id: 5, name: 'Excel', requiredLevel: 70 },
        { id: 6, name: 'Python', requiredLevel: 60 }
      ]
    },
    {
      id: 3,
      title: 'UX Designer',
      description: 'Creates user experiences that are intuitive and visually appealing.',
      requirements: [
        { id: 7, name: 'User Research', requiredLevel: 80 },
        { id: 8, name: 'Wireframing', requiredLevel: 70 },
        { id: 9, name: 'Figma', requiredLevel: 60 }
      ]
    }
  ];

  getSpecializations(): Observable<Specialization[]> {
    return of(this.specializations);
  }

  getSpecializationById(id: number): Observable<Specialization | undefined> {
    return of(this.specializations.find(s => s.id === id));
  }
}
