import { Component, OnInit } from '@angular/core';
import { DataService, Specialization } from '../../services/data.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-specializations',
  templateUrl: './specializations.component.html',
  styleUrls: ['./specializations.component.scss']
})
export class SpecializationsComponent implements OnInit {
  specializations: Specialization[] = [];

  constructor(private dataService: DataService, private router: Router) {}

  ngOnInit() {
    this.dataService.getSpecializations().subscribe((data) => {
      console.log('Fetched specializations:', data); // ✅ لمراقبة النتيجة
      this.specializations = data;
    });
  }

  goToSkills(id: number) {
    this.router.navigate(['/skills', id]);
  }
}
<div class="specializations-container">
  <h2>اختر تخصصك</h2>
  <div class="specialization-list">
    <div
      class="card"
      *ngFor="let s of specializations"
      (click)="goToSkills(s.id)"
    >
      <h3>{{ s.title }}</h3>
      <p>{{ s.description }}</p>
    </div>
  </div>
</div>
