import { Component } from '@angular/core';
import { Router } from '@angular/router';

interface Specialization {
  name: string;
  requiredSkills: string[];
  recommendedCourses: string[];
}

@Component({
  selector: 'app-result',
  templateUrl: './result.component.html',
  styleUrls: ['./result.component.scss']
})
export class ResultComponent {
  userSkills: string[] = [];
  bestMatch: Specialization | null = null;
  matchPercentage: number = 0;
  missingSkills: string[] = [];

  specializations: Specialization[] = [
    {
      name: 'ÿ™ÿ∑ŸàŸäÿ± ÿßŸÑŸàŸäÿ®',
      requiredSkills: ['HTML', 'JavaScript', 'SQL', 'UX Design'],
      recommendedCourses: [
        'Modern Web Development Bootcamp',
        'JavaScript Mastery',
        'Frontend Developer Professional Certificate'
      ]
    },
    {
      name: 'ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™',
      requiredSkills: ['Python', 'SQL', 'Data Analysis', 'Critical Thinking'],
      recommendedCourses: [
        'Google Data Analytics',
        'Python for Data Science',
        'Excel to MySQL: Analytic Techniques'
      ]
    },
    {
      name: 'ÿßŸÑÿ£ŸÖŸÜ ÿßŸÑÿ≥Ÿäÿ®ÿ±ÿßŸÜŸä',
      requiredSkills: ['Cybersecurity', 'Problem Solving', 'Leadership'],
      recommendedCourses: [
        'Introduction to Cybersecurity',
        'CompTIA Security+',
        'Cyber Threat Management'
      ]
    },
    {
      name: 'ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÖÿ¥ÿßÿ±Ÿäÿπ',
      requiredSkills: ['Leadership', 'Communication', 'Time Management', 'Teamwork'],
      recommendedCourses: [
        'Google Project Management',
        'Agile Project Management',
        'PMP Certification Prep'
      ]
    },
    {
      name: 'ÿßŸÑÿ≠Ÿàÿ≥ÿ®ÿ© ÿßŸÑÿ≥ÿ≠ÿßÿ®Ÿäÿ©',
      requiredSkills: ['Cloud Computing', 'Problem Solving', 'Communication'],
      recommendedCourses: [
        'AWS Cloud Practitioner Essentials',
        'Azure Fundamentals',
        'Google Cloud Associate'
      ]
    }
  ];

  constructor(private router: Router) {
    const nav = this.router.getCurrentNavigation();
    const state = nav?.extras?.state as { userSkills: string[] };
    this.userSkills = state?.userSkills || [];

    if (this.userSkills.length > 0) {
      this.calculateBestMatch();
    }
  }

  calculateBestMatch() {
    let bestMatchValue = 0;
    let bestMatchSpec: Specialization | null = null;

    for (const spec of this.specializations) {
      const matchedSkills = spec.requiredSkills.filter(s => this.userSkills.includes(s));
      const matchPercent = (matchedSkills.length / spec.requiredSkills.length) * 100;

      if (matchPercent > bestMatchValue) {
        bestMatchValue = matchPercent;
        bestMatchSpec = spec;
      }
    }

    this.bestMatch = bestMatchSpec;
    this.matchPercentage = Math.round(bestMatchValue);

    if (this.bestMatch) {
      this.missingSkills = this.bestMatch.requiredSkills.filter(
        s => !this.userSkills.includes(s)
      );
    }
  }

  restart() {
    this.router.navigate(['/skills']);
  }
}
